import React, { useState } from "react";
import '../styleContact.css'
const Contacts = () => {
  const [email, setEmail] = useState("");
  const [isValid, setIsValid] = useState(true);

  const handleChange = (event) => {
    const { value } = event.target;
    setEmail(value);
    setIsValid(validateEmail(value));
  };

  const validateEmail = (email) => {
    // Expresi칩n regular para validar el formato del correo
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  return (
    <section class="container">
    <article class="col s6 offset-s3">
      <form method="POST" action="formulario-contacto.php">
        <div class="input-field">
          <i class="material-icons prefix">perm_identity</i>
          <label for="nombre">Nombre</label>
          <input type="text" value={email} onchange={handleChange}required />
        </div>

        <div class="input-field">
          <i class="material-icons prefix">person_pin</i>
          <label for="apellido">Apellido</label>
          <input type="text" required />
        </div>

        <div class="input-field">
          <i class="material-icons prefix">email</i>
          <label for="email">Email</label>
          <input type="email" name="email" required />
        </div>

        <div class="input-field">
          <i class="material-icons prefix">mode_edit</i>
          <label for="mensaje">Mensaje</label>
          <textarea name="mensaje" id="" rows="10" class="materialize-textarea" length="140" required/>
        </div>
        <p class="center-align">
          <button class="waves-effect waves-light btn" type="submit">
            <i class="material-icons right">send</i>enviar
          </button>
        </p>
      </form>
    </article>
  {isValid ? (
        <p>El correo es v치lido.</p>
      ) : (
        <p style={{ color: "red" }}>Por favor, ingresa un correo v치lido.</p>
      )}
</section>
      

  );
};

export default Contacts;
