import React, { useState } from 'react';
import axios from 'axios';
import emailjs from 'emailjs-com'; // Importar emailjs-com
import '../styleForm.css';

function Formulario() {
  const [nombre, setNombre] = useState('');
  const [apellido, setApellido] = useState('');
  const [tel, setTel] = useState('');
  const [mail, setMail] = useState('');
  const [asunto, setAsunto] = useState('');
  const [mensaje, setMensaje] = useState('');
  const [telError, setTelError] = useState('');
  const [mailError, setMailError] = useState('');
  const [submissionStatus, setSubmissionStatus] = useState(null);

  const validatePhone = (phone) => {
    const phoneRegex = /^\d{9}$/;
    return phoneRegex.test(phone);
  };

  const validateEmail = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    if (!validatePhone(tel)) {
      setTelError('El teléfono debe ser un número de 9 dígitos.');
      return;
    }

    if (!validateEmail(mail)) {
      setMailError('El correo electrónico no es válido.');
      return;
    }

    setTelError('');
    setMailError('');

    const formData = {
      nombre,
      apellido,
      tel,
      mail,
      asunto,
      mensaje,
    };

    // Reemplaza 'user_id', 'service_id' y 'template_id'
    // con los valores reales proporcionados por EmailJS
    const form = useRef();

    const sendEmail = (e) => {
      e.preventDefault();
  
      emailjs.sendForm('YOUR_SERVICE_ID', 'YOUR_TEMPLATE_ID', form.current, 'YOUR_PUBLIC_KEY')
        .then((result) => {
            console.log(result.text);
        }, (error) => {
            console.log(error.text);
        });
      }}
  
  return (
      <form ref={form} onSubmit={sendEmail}>
        <label>Name</label>
        <input type="text" name="user_name" />
        <label>Email</label>
        <input type="email" name="user_email" />
        <label>Message</label>
        <textarea name="message" />
        <input type="submit" value="Send" />
      </form>
    
  );
}

export default Formulario;
