import React, { useState } from 'react';

const Contacts = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');
  const [isEmailValid, setIsEmailValid] = useState(true);

  const handleNameChange = (event) => {
    setName(event.target.value);
  };

  const handleEmailChange = (event) => {
    const { value } = event.target;
    setEmail(value);
    setIsEmailValid(validateEmail(value));
  };

  const handleMessageChange = (event) => {
    setMessage(event.target.value);
  };

  const validateEmail = (email) => {
    // Expresión regular para validar el formato del correo
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    // Validar el correo antes de enviar el formulario
    const isFormValid = validateEmail(email);

    if (isFormValid) {
      // Aquí puedes realizar alguna acción con los datos del formulario, como enviarlos a un servidor, por ejemplo.
      console.log('Datos del formulario válidos:', { name, email, message });

      // Limpiar el formulario después de enviar los datos
      setName('');
      setEmail('');
      setMessage('');
    } else {
      console.log('Por favor, ingresa un correo válido.');
    }
  };

  return (
    <div>
      <h1>Formulario de Contacto</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Nombre:</label>
          <input type="text" value={name} onChange={handleNameChange} required />
        </div>
        <div>
          <label>Correo:</label>
          <input
            type="text"
            value={email}
            onChange={handleEmailChange}
            style={{ borderColor: isEmailValid ? 'green' : 'red' }}
            required
          />
          {!isEmailValid && <p style={{ color: 'red' }}>Por favor, ingresa un correo válido.</p>}
        </div>
        <div>
          <label>Mensaje:</label>
          <textarea value={message} onChange={handleMessageChange} required />
        </div>
        <button type="submit">Enviar</button>
      </form>
    </div>
  );
};


export default Contacts;
