import React from 'react';
import CatalogList from './CatalogList';

const Catalog = ({ items, filtro }) => {
  const filteredItems = filtro === 'todos' ? items : items.filter(item => item.name.includes(filtro));


  return (
    <div className="catalog">
      <div className="filtro">
        <button onClick={() => setFiltro('todos')}>Todos</button>
        <button onClick={() => setFiltro('AMD')}>AMD</button>
        <button onClick={() => setFiltro('Intel')}>Intel</button>
      </div>
      <CatalogList items={filteredItems} />
    </div>
  );
};

export default Catalog;
