// import { Logo } from '../assets/Logo';
import Logo from '../assets/Logo.svg';
// Intento de bajada para aparecer
import React, { useEffect } from 'react';
import '../s'; // Asegúrate de reemplazar con la ruta correcta a tu archivo CSS

const ScrollImageComponent = () => {
  useEffect(() => {
    const handleScroll = () => {
      const elements = document.querySelectorAll('.fade-in-image');
      elements.forEach((element) => {
        const elementTop = element.getBoundingClientRect().top;
        const windowHeight = window.innerHeight;

        if (elementTop < windowHeight * 0.8) {
          element.classList.add('fade-in'); // Agrega la clase para hacer aparecer la imagen
        }
      });
    };

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);



    return(
        <main>
            <section className="container section-1">
                <div className="slogan">
                    <h1 className="company-title">LongDino</h1>
                    <h2 className="company-slogan">
                        Somos una Pyme dedicada al armado de Pc y soporte tecnico en distintas areas, ademas hacemos instalaciones de cableado de red, no dudes en contactarnos por tus consultas.
                    </h2>
                </div>
                        <img class="fade-in-image" className="cont1" src={Logo} alt="Aqui va la img"/>
                    {/* Agregar imagen del slogan al terminar el diseño*/}
                
            </section>
        </main>
    );
}
export default ScrollImageComponent;
