import React, { useState } from 'react';
import axios from 'axios';

const ContactForm = () => {
  const [name, setName] = useState('');
  const [apellido, setApellido] = useState('');
  const [tel, setTel] = useState('');
  const [email, setEmail] = useState('');
  const [subject, setSubject] = useState('');
  const [message, setMessage] = useState('');
  const [isEmailValid, setIsEmailValid] = useState(true);

  const handleNameChange = (event) => {
    setName(event.target.value);
  };

  const handleApellidoChange = (event) => {
    setApellido(event.target.value);
  };

  const handleTelChange = (event) => {
    setTel(event.target.value);
  };

  const handleEmailChange = (event) => {
    const emailValue = event.target.value;
    setEmail(emailValue);

    // Validación del correo electrónico utilizando una expresión regular simple
    const emailPattern = /^\S+@\S+\.\S+$/;
    setIsEmailValid(emailPattern.test(emailValue));
  };

  const handleSubjectChange = (event) => {
    setSubject(event.target.value);
  };

  const handleMessageChange = (event) => {
    setMessage(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    // Verificar si el correo electrónico es válido antes de enviar el formulario
    if (!isEmailValid) {
      return;
    }

    // Objeto con los datos a enviar al servidor
    const formData = {
      nombre,
      apellido,
      tel,
      mail: email,
      asunto: subject,
      mensaje: message,
    };

    // Enviar la solicitud POST utilizando Axios
    axios.post('/api/formulario', formData)
      .then((response) => {
        console.log(response.data); // Mensaje de éxito del servidor
        // Puedes hacer algo más aquí, como mostrar un mensaje de éxito al usuario.
      })
      .catch((error) => {
        console.error('Error al enviar el formulario:', error);
        // Puedes mostrar un mensaje de error al usuario si la inserción falla.
      });
  };

  return (
    <div className="contact-container">
      <div className="contenido">
        <div>
          <h1>Formulario de Contacto</h1>
        </div>
        <form className="formulario" name="formulario" onSubmit={handleSubmit}>
          {/* ... (Resto del formulario) ... */}
        </form>
      </div>
    </div>
  );
};

export default ContactForm;
