import React, { useState } from 'react';
const PasswordValidationExample = () => {
    const [password, setPassword] = useState('');
    const [isValid, setIsValid] = useState(true);
  
    const handleChange = (event) => {
      const { value } = event.target;
      setPassword(value);
      setIsValid(validatePassword(value));
    };
  
    const validatePassword = (password) => {
      // Verificar si la longitud de la clave es de al menos 6 dígitos
      return password.length >= 6;
    };
}
const Contacts = () => {
    const [email, setEmail] = useState('');
    const [isValid, setIsValid] = useState(true);
  
    const handleChange = (event) => {
      const { value } = event.target;
      setEmail(value);
      setIsValid(validateEmail(value));
    };
  
    const validateEmail = (email) => {
      // Expresión regular para validar el formato del correo
      const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
      return emailRegex.test(email);
    };
}

/*Validacion de Correo*/

/*Validacion de Clave*/
function forms(){
  return (
    <div>
      <h1>Validación de Correo en React</h1>
      <input
        type="text"
        value={email}
        onChange={handleChange}
        style={{ borderColor: isValid ? 'green' : 'red' }}
      />
      {isValid ? (
        <p>El correo es válido.</p>
      ) : (
        <p style={{ color: 'red' }}>Por favor, ingresa un correo válido.</p>
      )}
    </div>
  );
};

export default Contacts;
