import React, { useState, useEffect } from "react";

const Greeting = () => {
  const [time, setTime] = useState(new Date());

  useEffect(() => {
    const intervalId = setInterval(() => {
      setTime(new Date());
    }, 1000);

    // Limpia el intervalo cuando el componente se desmonta
    return () => clearInterval(intervalId);
  }, []);

  const hours = time.getHours();
  let greeting;

  if (hours >= 0 && hours < 12) {
    greeting = "Buenos días, Charlemos por WhatsApp";
  } else if (hours >= 12 && hours < 18) {
    greeting = "Buenas tardes, Charlemos por WhatsApp";
  } else {
    greeting = "Buenas noches, Charlemos por WhatsApp";
  }

  return (
    <div class="wt">
      <div class="popup">
        <p>{Greeting}</p>
      </div>
      <div class="wt-icon">
        <a target="_blank" href="#">
          <svg viewBox="0 0 46.4 45.9">
            <img src="https://i.ibb.co/VgSspjY/whatsapp-button.png" />
          </svg>
        </a>
      </div>
    </div>
  );
};

export default Greeting;
