import React from "react";


const CatalogItem = ({ item }) => {
  const formattedDescription = item.description
  .replace(/<br\/>/g, '\n')
  .replace(/<.*?>/g, '');
  return (
    <div class="catalog__col">
      <div class="catalog__card card">
        <div class="card__image">
<img src={require(`../../assets/Productos/${item.imageFileName}`)} alt={item.name}/>
        </div>
        <div class="card__body">
          <div class="card__title">{item.name}</div>
          <div class="shopline"></div>
          <div class="card__text">
          <p dangerouslySetInnerHTML={{ __html: item.description }} />
          </div>
          <div class="card__bottom">
            <div class="card__cost">{item.price}</div>
            <div class="card__button button">
              <a
                href={`https://api.whatsapp.com/send?phone=+56936681029&text=Hola%2C%20quiero%20comprar%20un%20PC%20con%20las%20siguientes%20característica${encodeURIComponent(item.wspMesagge)}`}
              >
                Solicitar
              </a>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CatalogItem;
