import React, { useState } from 'react';

const Contacts = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isEmailValid, setIsEmailValid] = useState(true);
  const [isPasswordValid, setIsPasswordValid] = useState(true);

  const handleEmailChange = (event) => {
    const { value } = event.target;
    setEmail(value);
    setIsEmailValid(validateEmail(value));
  };

  const handlePasswordChange = (event) => {
    const { value } = event.target;
    setPassword(value);
    setIsPasswordValid(validatePassword(value));
  };

  const validateEmail = (email) => {
    // Expresión regular para validar el formato del correo
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const validatePassword = (password) => {
    // Verificar si la longitud de la clave es de al menos 6 dígitos
    return password.length >= 6;
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    // Validar ambos campos antes de enviar el formulario
    const isFormValid = validateEmail(email) && validatePassword(password);

    if (isFormValid) {
      // Aquí puedes realizar alguna acción con los datos ingresados, como enviarlos a un servidor, por ejemplo.
      console.log('Datos válidos:', { email, password });
    } else {
      console.log('Datos inválidos. Por favor, verifica el correo y la clave.');
    }
  };

  return (
    <div className=''>
      <h1>Validación de Correo y Clave en React</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Correo:</label>
          <input
            type="text"
            value={email}
            onChange={handleEmailChange}
            style={{ borderColor: isEmailValid ? 'green' : 'red' }}
          />
        </div>
        <div>
          <label>Clave:</label>
          <input
            type="password"
            value={password}
            onChange={handlePasswordChange}
            style={{ borderColor: isPasswordValid ? 'green' : 'red' }}
          />
        </div>
        <button type="submit">Enviar</button>
      </form>
      {!isEmailValid && <p style={{ color: 'red' }}>Por favor, ingresa un correo válido.</p>}
      {!isPasswordValid && (
        <p style={{ color: 'red' }}>La clave debe tener al menos 6 dígitos.</p>
      )}
    </div>
  );
};

export default Contacts;
