import React, { useState } from 'react';
import axios from 'axios';

const Contact = () => {
  // Aquí van los estados para los campos del formulario
  const [name, setName] = useState('');
  const [apellido, setApellido] = useState('');
  const [tel, setTel] = useState('');
  const [email, setEmail] = useState('');
  const [subject, setSubject] = useState('');
  const [message, setMessage] = useState('');
  const [isEmailValid, setIsEmailValid] = useState(true);

  const handleSubmit = (event) => {
    event.preventDefault();

    // Realiza la validación del correo electrónico antes de enviar los datos al servidor
    if (!validateEmail(email)) {
      setIsEmailValid(false);
      return;
    }

    // Si el correo es válido, envía los datos al servidor usando Axios
    axios.post('http://localhost:3001/api/contacto', {
      nombre: name,
      apellido: apellido,
      tel: tel,
      mail: email,
      asunto: subject,
      mensaje: message,
    })
      .then((response) => {
        console.log(response.data); // Datos guardados correctamente en el servidor
        // Aquí puedes mostrar algún mensaje de éxito al usuario
      })
      .catch((error) => {
        console.error('Error al guardar los datos:', error);
        // Aquí puedes mostrar algún mensaje de error al usuario
      });
  };

  const handleNameChange = (event) => {
    setName(event.target.value);
  };

  // Aquí van los demás manejadores de cambio de estado para los otros campos del formulario

  // Función de validación de correo electrónico
  const validateEmail = (email) => {
    const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return re.test(email);
  };
  
  return (
<div className="contact-container">
  <div className="contenido">
    <div>
      <h1>Formulario de Contacto</h1>
    </div>
    <form className="formulario" name="formulario" onSubmit={handleSubmit}>
      <div className="contacto">
        <label htmlFor="nombre">Nombre: </label>
        <input
          type="text"
          placeholder="Jhon"
          required
          value={name}
          onChange={handleNameChange}
        />
        <label htmlFor="apellido">Apellido: </label>
        <input
          type="text"
          placeholder="Wick"
          required
          value={apellido}
          onChange={handleApellidoChange}
        />

        <label htmlFor="tel">Telefono: </label>
        <input
          type="tel"
          required
          value={tel}
          onChange={handleTelChange}
        />
        <label htmlFor="mail">Mail: </label>
        <input
          type="email"
          value={email}
          onChange={handleEmailChange}
          required
        />
        {!isEmailValid && (
          <p style={{ color: "red" }}>Por favor, ingresa un correo válido.</p>
        )}
      </div>
      <div className="work">
        <h2>Asunto</h2>
        <textarea
          type="text"
          maxLength="300"
          value={subject}
          onChange={handleSubjectChange}
        ></textarea>
      </div>
      <div className="descripcion">
        <h2>Mensaje: </h2>
        <textarea
          type="text"
          maxLength="300"
          value={message}
          onChange={handleMessageChange}
          placeholder="Cuentanos de que se trata..."
        ></textarea>
      </div>
      <div className="botones">
        <button type="submit" form="formulario" value="Submit">
          Enviar
        </button>
      </div>
    </form>
  </div>
</div>
  );
};

export default Contact;
